countries={}
def isascii(s):
    return len(s) == len(s.encode())

with open('world_cities.txt', encoding="utf8") as csv_file:
    csv_reader = csv.reader(csv_file, delimiter=',')
    for row in csv_reader:
        city=row[0]
        country=row[1]
        print(city)
        if country in country_list and isascii(city):
            if country in countries:
                countries[country].append(city)
            else:
                countries[country]=[city]

with open('countries.json', 'w') as fp:
    json.dump(countries, fp)
